function params = displayParams% For UMC Utrecht%    MICROSOFT SURFACE PROparams.numPixels    = [2160 1440];   params.dimensions   = [25.4 15.24];  % based on diagonal size of 21.5 inches = 54.61 cmparams.distance     = 50;           params.frameRate    = 60;           params.cmapDepth    = 8;            params.screenNumber = 0; % Changing this to 0 does not affect the stimulus appearance 