function params = displayParams% This file corresponds to a high resolution hypothetical display (5000 x% 5000) pixels. For the BAIR experiments, we make stimuli for this display% and the resample according to the pixel size of the various displays% for the project.%% Critical parametersradiusInDegrees     = 8.3; params.numPixels    = [5000 5000];params.dimensions   = radiusInDegrees * [2 2]; % cmparams.distance     = radiusInDegrees/tan(radiusInDegrees * pi / 180);params.frameRate    = 60;params.cmapDepth    = 8;params.screenNumber = 1;% Descriptive parametersparams.position = 'None';% parameters which can make programming the display easierparams.flipLR = 0;params.flipUD = 0;