function params = displayParams% % % CODE TO SAVE GAMMA TABLE% Gamma table should be linear%  gamma = (ones(3,1)*(0:255)/255)';%  gammaTable = (ones(3,1)*(0:255))';% %% % where to save?% pth = fullfile(vistadispRootPath, 'exptTools', 'displays','CBI_Propixx');% %% save(fullfile(pth, 'gamma'), 'gamma', 'gammaTable');% Critical parametersparams.numPixels    = [1024 768]; % this is what's on the wiki; online description of BOLDscreen 3D says it can go up to 1920x1080params.dimensions   = [32.5 52.0]; % cmparams.distance     = 112.0;params.frameRate    = 60;params.cmapDepth    = 8;params.screenNumber = 1;% Descriptive parametersparams.position = 'UMC_3TLCD';params.stereoFlag = 0;% parameters which can make programming the display easierparams.flipLR = 0;params.flipUD = 0;