function params = displayParams%% % CODE TO SAVE GAMMA TABLE% % load in a measurement made from PTB's CalibrateMonitorPhotometer% load('eikiLCXG250_140603_CalMonPhotom_33point')%% % use gammaTable 1, which is the powerlaw fit to the measurements% g = gammaTable1;%% % replicate to 256 x 3 for RGB (assuming 8 bits)% gamma = [g g g];%% % integers for lookup table% gammaTable = round(gamma * 255);%% % where to save?% pth = '~/matlab/git/vistadisp/exptTools2/displays/meg_lcddisplayParams';%% save('gamma', 'gamma', 'gammaTable');% Critical parametersparams.numPixels    = [1024 768];params.dimensions   = [32 24]; % cmparams.distance     = 58;params.frameRate    = 60;params.cmapDepth    = 8;params.screenNumber = 1;% Descriptive parametersparams.position = 'CBI projector';params.stereoFlag = 0;% parameters which can make programming the display easierparams.flipLR = 0;params.flipUD = 0;